mapper.select:
SELECT

FROM
way_robot_action_scope


		use_type = #{useType,jdbcType=TINYINT}


		AND use_id = #{useId,jdbcType=VARCHAR}


		AND scope_type = #{scopeType,jdbcType=TINYINT}


		AND scope_id = #{scopeId,jdbcType=VARCHAR}


		AND subsystem_id = #{subsystemId,jdbcType=VARCHAR}


		AND modified_by = #{modifiedBy,jdbcType=BIGINT},


ORDER BY modified_time DESC

select robot_id id, nick_name NAME, 1 type from way_robot_manage where 1=2
UNION select robot_id id, nick_name NAME, 1 type FROM way_robot_manage where is_deleted = 0
AND subsystem_id = #{subsystemId,jdbcType=VARCHAR}
AND modified_by = #{modifiedBy,jdbcType=BIGINT}
UNION select robot_type_id id, robot_type_name NAME, 2 type FROM way_robot_type where is_deleted = 0
AND subsystem_id = #{subsystemId,jdbcType=VARCHAR}
AND modified_by = #{modifiedBy,jdbcType=BIGINT}
UNION select install_site_id id, site_name NAME, 3 type FROM way_robot_install_site where is_deleted = 0
AND subsystem_id = #{subsystemId,jdbcType=VARCHAR}
AND modified_by = #{modifiedBy,jdbcType=BIGINT}
